			while(d < mn){
				if(i + 1 >= n) goto SKIP2;
				d *= 10;
				d += s[++i] -'0';
			}
			mx = max(mx, d);
			if(mx - mn >= ans) goto SKIP2;
		}
		ans = min(ans, mx - mn);
		SKIP2:;
	}
	cout << ans << endl;

	return 0;
}

