vector<long long int> divisor(long long int n) {
	vector<long long int> res;
	for (long long int i = 1; i * i <= n; ++i) {
		if (n % i == 0) {
			res.push_back(i);
			if (i * i != n) res.push_back(n / i);
		}
	}
	sort(begin(res), end(res));
	return res;
}

int get_sa(string a, string b) {
	int asize(a.size());
	bool flag=false;
	bool rev_flag=false;
	{
		for (int i = 0; i < asize; ++i) {
			if (!flag) {
				if (a[i] == b[i])continue;
				else {
					if (a[i] > b[i]) {
						swap(a, b);
						rev_flag=true;
					}
					assert(a[i]<b[i]);
					if (i == asize - 1) {
						if (rev_flag) {
							return -(b[i]-a[i]);
						}
						else {

							return b[i] - a[i];
						}
					}
					if (a[i] + 2 <= b[i]) {
						return 10000;
					}
					else {
						assert(a[i] + 1 == b[i]);
						flag=true;
					}
				}
			}
			else {
				if (a[i] == '9'&&b[i] == '0') {
					continue;
				}
				else {
					if (i >= asize - 3) {
						int anum=stoi(a.substr(i));
						int bnum=stoi("1"+b.substr(i));
						if (rev_flag) {
							return -(bnum-anum);
						}
						else {

							return bnum - anum;
						}
					}
					else {
						return 10000;
					}
				}
			}

		}
	}
	if (flag) {
		if (rev_flag) {
			return -1;
		}
		else {
			return 1;
		}
	}
	return 0;
}

