	for (int i = 2; i < S.size(); i++) {
		vector<long long>G;
		long long vase = getans((int)S.size() - i, (int)S.size()); G.push_back(vase);
		int cx = S.size() - i; bool flag = true;
		while (cx > 0) {
			long long t1 = getans(cx - (i - 1), cx);
			long long t2 = getans(cx - (i + 0), cx);
			long long t3 = getans(cx - (i + 1), cx);
			if (check(t1, vase) <= pos) { cx -= (i - 1); G.push_back(t1); }
			else if (check(t2, vase) <= pos) { cx -= (i + 0); G.push_back(t2); }
			else if (check(t3, vase) <= pos) { cx -= (i + 1); G.push_back(t3); }
			else { flag = false; break; }
		}
		if (flag == true) {
			sort(G.begin(), G.end()); G.erase(unique(G.begin(), G.end()), G.end());
			for (int j = 0; j < G.size(); j++) {
				for (int k = j + 1; k < G.size(); k++) {
					if (check(G[j], G[k]) > pos) flag = false;
				}
			}
			if (flag == true) {
				return true;
			}
		}
	}
	return false;
}

