  cin>>s;
  n=s.size();
  int ans=check(n);
  for(int i=2;i*i<=n;i++){
    if(n%i==0){
      ans=min(ans, check(i) );
      ans=min(ans, check(n/i) );
    }
  }

  cout<< min(ans,solve()) <<endl;
  return 0;
}

