int inf = 2000000000;
vvel dp;
int solve(vvel &sum, int i, int j, bool is_f) {
	int h = sum.size(); h--;
	int w = sum[0].size();
	if (i == h || j == w) { return 0; }
	if (dp[i][j] == inf) {
		int ans1 = solve(sum, i + 1, j, !is_f);
		int ans2 = solve(sum, i, j + 1, !is_f);
		ans2 += sum[i][j];
		if (is_f) {
			dp[i][j] = min(ans1, ans2);
		}
		else {
			dp[i][j] = max(ans1, ans2);
		}
	}
	return dp[i][j];
}
int rui10(int a) {
	if (a == 0) { return 1; }
	return 10 * rui10(a - 1);
}
int gcd(int a, int b) {
	if (a < b) { swap(a, b); }
	if (b == 0) { return a; }
	return gcd(b, a%b);
}
bool sol(int mn, int mx, vel &a) {
	int n = a.size();
	veb dp(n + 1, false); dp[0] = true;
	for (int i = 0; i < n; i++) {
		if (dp[i]) {
			if (mn <= a[i] and a[i] <= mx) {
				dp[i + 1] = true;
			}
			if (i + 2 <= n) {
				int num = a[i] * 10 + a[i+1];
				if (mn <= num and num <= mx) {
					dp[i + 2] = true;
				}
			}
		}
	}
	return dp[n];
}
