	for(int len = 3; len * 2 <= n; len++) if(n % len == 0){
		int mx = 0, mn = 99, d;
		for(int i = 0; i < n; i += len){
			if(!eq(i, i + len - 2, 0, len - 2, s)) goto SKIP;
			d = (s[i + len - 2] - '0') * 10 + (s[i + len - 1] - '0');
			mx = max(mx, d);
