public class hello
{
    public static int sL;
    public static string s;
    public static void Main()
    {
        s = Console.ReadLine().Trim();
        sL = s.Length;
        var c1 = calc1();
        if (c1 == 0) { Console.WriteLine(0); goto exit; }
        var c2 = calc2();
        Console.WriteLine(Min(c1, c2));
    exit:;
    }
    static int calc2()
    {
        var p = 0;
        var t2 = 0;
        var t1 = 10;
        while (p < sL)
        {
            if (s[p] == '1')
            {
                if (p + 1 < sL)
                    t2 = Max(t2, 10 + int.Parse(s[p + 1].ToString()));
                p += 2;
            }
            else
            {
                t1 = Min(t1, int.Parse(s[p].ToString()));
                p++;
            }
        }
        if (t1 == 10 | t2 == 0) return 10;
        else return t2 - t1;
    }
    static int getdev1()
    {
        var smax = 0;
        var smin = 10;
        for (int i = 1; i <= 9; i++)
        {
            if (s.Contains(i.ToString()))
            {
                smax = Max(smax, i);
                smin = Min(smin, i);
            }
        }
        return smax - smin;
    }
    static int getdevN(int n, int dev1)
    {
        var temp = new string[n];
        for (int i = 0; i < n; i++)
        {
            var w = sL / n;
            var t = s.Substring(w * i, w);
            temp[i] = t;
        }

        StringComparer cmp = StringComparer.OrdinalIgnoreCase;
        Array.Sort(temp, cmp);

        var ch = checkBI(temp[n - 1], temp[0]);
        if (ch == -1) return -1;
        if (ch < dev1) return ch;
        else return -1;
    }
    static int checkBI (string a , string b)
    {
        var aL = a.Length;
        if (aL == 2)
        {
            var t = int.Parse(a) - int.Parse(b);
            return t <= 7 ? t : -1;
        }

        var a1 = a.Substring(0, aL - 2);
        var b1 = b.Substring(0, aL - 2);
        if (a1 != b1) return -1;
        var a2 = int.Parse(a.Substring(aL - 2));
        var b2 = int.Parse(b.Substring(aL - 2));
        var t2 = a2 - b2;
        return t2 <= 7 ? t2 : -1;
    }
    static int calc1()
    {
        var dev = new List<int>();
        for (int i = 2; i * i <= sL; i++)
        {
            if (sL % i == 0)
            {
                dev.Add(i);
                if (sL / i != i) dev.Add(sL / i);
            }
        }
        var dev1 = getdev1();
        var ans = dev1;
        foreach (var x in dev)
        {
            var t = getdevN(x, dev1);
            if (t != -1) ans = Min(ans, t);
            if (ans == 0) return 0;
        }
        return ans;
    }
}

