  def this(s : Int) = this(s, s)
  lazy val size = top - bottom + 1
  def compare(that : Cup) : Int = this.bottom - that.bottom
  def +(other : Cup) : Cup = {
    (this < other) match {
      case true  => new Cup(bottom, other.top)
      case false => new Cup(other.bottom, top)
    }
  }
  lazy val moveCost = (pow(top - bottom + 1, 1) - 1) / 2
  private lazy val pow : (Int, Int) => Int = (n, r) => {
    n match {
      case 0 => r
      case _ => pow(n - 1, 3 * r)
    }
  }
  def ^(other : Cup) : Boolean = (((other.bottom + 1) == top) || ((bottom + 1) == other.top))
  lazy val parity = bottom % 2
}
