using namespace std;

int m,INF=30000000;
stack<int,vector<int> >A,B,C;
stack<int,vector<int> >A2,B2,C2;

int func(string p){

  int cnt=0;

  for(int i=0;i<m;i++){

    if(B.empty() && (C.empty()||A.empty()))return cnt;

    if(!A.empty() && (B.empty() || A.top()>=B.top()) && p!="BA"){
      B.push(A.top());
      A.pop();
      cnt++,p="AB";
    }

    if(!B.empty()){
      if((C.empty() || B.top()>=C.top()) && p!="CB"){
	C.push(B.top());
	B.pop();
	cnt++,p="BC";
      }

      if((A.empty() || B.top()>=A.top()) && p!="AB"){
	A.push(B.top());
	B.pop();
	cnt++,p="BA";
      }
    }

    if(!C.empty() && (B.empty() || C.top()>=B.top()) && p!="BC"){
      B.push(C.top());
      C.pop();
      cnt++,p="CB";
    }

  }

  return INF;
}

int main(void){
  int n,ans,s,t;

  while(cin >> n >> m,n|m){
    while(!A.empty())A.pop();
    while(!B.empty())B.pop();
    while(!C.empty())C.pop();
    while(!A2.empty())A2.pop();
    while(!B2.empty())B2.pop();
    while(!C2.empty())C2.pop();


    cin >> s;
    for(int i=0;i<s;i++){
      cin >> t;
      A.push(t);
    }
    cin >> s;
    for(int i=0;i<s;i++){
      cin >> t;
      B.push(t);
    }
    cin >> s;
    for(int i=0;i<s;i++){
      cin >> t;
      C.push(t);
    }

    A2=A,B2=B,C2=C;

    ans=func("CB");

    A=A2,B=B2,C=C2;
    ans=min(ans,func("AB"));

    A=A2,B=B2,C=C2;
    ans=min(ans,func("BA"));

    A=A2,B=B2,C=C2;
    ans=min(ans,func("BC"));

    if(ans==INF)cout << -1 << endl;
    else cout << ans << endl;

  }
  return 0;
}

#include <cstdio>

int nn = 1, an, bn, cn, a[20], b[20], c[20], total = 0, f = 1;

struct mm{
	char ch;
	int nu;
};

struct mm m1, m2;

int calculation(int min, int max){
	int sum;
	if(min == max)return 0;
	if(min < max)sum = calculation(++min, max) * 3 + 2;
	return sum;
}

int move(int flag){
	int number;
	if(flag == 0){
		number = an - m1.nu + 1;
		for(int i = 0;i < number;i++)c[++cn] = a[an--];
	}
	if(flag == 1){
		number = cn - m1.nu + 1;
		for(int i = 0;i < number;i++)a[++an] = c[cn--];
	}
	calculation(0, number);
	return 0;
}

int sort(){
	if(m1.ch == m2.ch)return 0;
	if(m2.ch == 'b'){
		if(m1.ch == 'a')c[++cn] = b[bn--], move(0); //???
		if(m1.ch == 'c')a[++an] = a[an--], move(1); //???
	}
	return 0;
}

int solve(int max){
	for(int i = 0;i < an;i++){
		if(a[i] == max)m1.ch = 'a',m1.nu = i;
		if(a[i] == max - 1)m2.ch = 'a', m2.nu = i;
	}
	for(int i = 0;i < bn;i++){
		if(b[i] == max)m1.ch = 'b',m1.nu = i;
		if(b[i] == max - 1)m2.ch = 'b', m2.nu = i;
	}
	for(int i = 0;i < cn;i++){
		if(c[i] == max)m1.ch = 'c',m1.nu = i;
		if(c[i] == max - 1)m2.ch = 'c', m2.nu = i;
	}
	if(f == 1 && m1.nu == bn){
		if(m2.ch == 'a'){
			a[++an] = b[bn--];
		}else if(m2.ch == 'c'){
			c[++cn] = b[bn--];
		}
	}else{
		sort();
	}
	return 0;
}

int main(){
	int n, m, max;
	int a[20], b[20], c[20];
	scanf("%d %d",&n, &m);

	scanf("%d", &an);
	for(int i = 0;i < an;i++){
		scanf("%d", &a[i]);
		if(a[i] > max)max = a[i];
	}
	scanf("%d", &bn);
	for(int i = 0;i < bn;i++){
		scanf("%d", &b[i]);
		if(b[i] > max)max = b[i];
	}
	scanf("%d", &cn);
	for(int i = 0;i < cn;i++){
		scanf("%d", &c[i]);
		if(c[i] > max)max = c[i];
	}

	solve(max);

	printf("%d %d",n, m);
	return 0;
}

