typedef long long ll;

struct Cups{
	std::vector<int> S[3];
	int t;
	Cups(const std::vector<int> (&_S)[3], int _t)
		:t(_t){
		for(int i=0;i<3;i++){
			for(int j=0;j<_S[i].size();j++){
				S[i].push_back(_S[i][j]);
			}
		}
	}
};

ll toInt(const std::vector<int> (&S)[3]){
	ll res = 0;
	for(int i=0;i<3;i++){
		for(int j=0;j<S[i].size();j++){
			int elem = S[i][j];
			res |= i << 2 * elem;
		}
	}
	return res;
}

void dou(const std::vector<int> (&S)[3]){
	for(int i=0;i<3;i++){
		for(int j=0;j<S[i].size();j++){
			std::cout << S[i][j] << " ";
		}
		std::cout << std::endl;
	}
}

int main(){
	int n, m;
	while(std::cin >> n >> m, n){
		std::vector<int> S[3];
		std::map<ll, int> used;
		for(int i=0;i<3;i++){
			int SN;
			std::cin >> SN;
			for(int j=0;j<SN;j++){
				int e;
				std::cin >> e;
				S[i].push_back(e-1);
			}
			std::sort(S[i].begin(), S[i].end(), std::greater<int>());
		}

		std::queue<Cups> q;
		q.push(Cups(S, 0));

		int res = -1;
		while(!q.empty()){
			Cups c = q.front();q.pop();
			std::vector<int> S[3] = c.S;
			int t = c.t;

			if(t > m || used[toInt(S)]){
				continue;
			}
			if(S[0].size() == n || S[2].size() == n){
				res = t;
				break;
			}

			used[toInt(S)] = 1;

			for(int i=0;i<3;i++){
				for(int j=0;j<3;j++){//i -> j
					if(std::abs(i-j) != 1 || S[i].empty() || (!S[j].empty() && S[i][0] < S[j][0]))continue;
					std::vector<int> _S[3] = S;
					_S[j].push_back(_S[i][0]);
					_S[i].erase(_S[i].begin());
					std::sort(_S[j].begin(), _S[j].end(), std::greater<int>());
					q.push(Cups(_S, t+1));
				}
			}
		}

		std::cout << res << std::endl;
	}
}

#include <bits/stdc++.h>
using namespace std;

int n, m;
const int dx[2] = {-1, 1};
int bit_power[15];

struct cup_state {
    vector<int> tray[3];
    int move_cnt = 0;
    void print(){
        cout << "cup_state :" << this << endl;
        for (auto i = 0; i < 3; i++) {
            cout << "[" << i << "] : ";
            for (int j : tray[i]) {
                cout << j << " ";
            }
            cout << endl;
        }
        cout << "move_cnt=" << move_cnt << endl;
    };
    int to_bit(){
        int res = 0;
        for (auto i = 0; i < 3; i++) {
            for (auto j = 0; j < tray[i].size(); j++) {
//                res += i << ((tray[i][j] - 1) * 2);
                res += i * bit_power[tray[i][j] - 1];
            }
        }
        return res;
    }
};

int bfs(cup_state init) {
    vector<bool> d((int)pow(3, 15), false);
    queue<cup_state> q;
    q.push(init);
    d[init.to_bit()] = true;
    while (not q.empty()) {
        cup_state p = q.front();
        q.pop();
//        p.print();
        for (auto i = 0; i < 3; i++) {
            if (p.tray[i].empty()) continue;
            if (i != 1 and p.tray[i].size() == n) {
//                cout << p.move_cnt << endl;
                return p.move_cnt;
            }
            if(p.move_cnt >= m) continue;
            for (int j : dx) {
                cup_state tmp = p;
//                cout << "tray : " << i << endl;
                int tmp_cup = p.tray[i][p.tray[i].size() - 1];
                if(tmp_cup == 1 and i != 1) continue;
//                cout << "tmp_cup : " << tmp_cup << endl;
                int tray_index = i + j;
                if (tray_index < 0 or tray_index >= 3) continue;
                if (not p.tray[tray_index].empty()
                    and tmp_cup < p.tray[tray_index][p.tray[tray_index].size() - 1]) continue;
//                cout << "next_tray : " << tray_index << endl;
                tmp.tray[tray_index].push_back(tmp_cup);
                tmp.tray[i].pop_back();
                tmp.move_cnt = p.move_cnt + 1;
//                tmp.print();
                if(not d[tmp.to_bit()]) {
                    q.push(tmp);
                    d[tmp.to_bit()] = true;
                }
            }
        }
    }
    return -1;
}
int main(){
    int p = 1;
    for (int & i : bit_power) {
        i = p;
        p *= 3;
    }

    while(true) {
        cin >> n >> m;
        if(n == 0 and m == 0) break;

        cup_state init = cup_state();
        for (auto &i : init.tray) {
            i = vector<int>(0);
            int size, tmp;
            cin >> size;
            for (auto j = 0; j < size; j++) {
                cin >> tmp;
                i.push_back(tmp);
            }
        }

        int ans = bfs(init);
        cout << ((ans <= m) ? ans : -1) << endl;
    }

    return 0;
}

