int const inf = 1<<29;

int N, M;

int solve(vector<int> const& target) {
  queue<vector<int>> q;
  int num = 0;
  rep(i, N) num |= 1 << i;
  q.push({num, 0, 0});
  q.push({0, 0, num});

  map<vector<int>, int> mp;
  mp[{num, 0, 0}] = 0;
  mp[{0, 0, num}] = 0;

  while(!q.empty()) {
    auto v = q.front(); q.pop();
    if(v == target) {
      return mp[v];
    }

    rep(i, v.size()) {
      for(int k=N-1; k>=0; k--) {
        if(v[i] >> k & 1) {
          if(i + 1 < 3) {
            auto nv = v;
            nv[i] ^= 1 << k;
            if(nv[i + 1] < (1<<k)) {
              nv[i + 1] ^= 1 << k;
              if(mp.find(nv) == mp.end() && mp[v] + 1 <= M) {
                mp[nv] = mp[v] + 1;
                q.push(nv);
              }
            }
          }

          if(i - 1 >= 0) {
            auto nv = v;
            nv[i] ^= 1 << k;
            if(nv[i - 1] < (1<<k)) {
              nv[i - 1] ^= 1 << k;
              if(mp.find(nv) == mp.end() && mp[v] + 1 <= M) {
                mp[nv] = mp[v] + 1;
                q.push(nv);
              }
            }
          }

          break;
        }
      }
    }
  }

  return -1;
}

