    res = "";
    for (int i = 0; i < 3; i++) {
        for (auto j : v[i]) {
            if (j < 0) continue;
            res = res + (char)('a' + j);
        }
        for (int j = v[i].size(); j <= n; j++) {
            res = res + '0';
        }
    }
    return res;
}

void hoge(mat &v, vector<mat> &next, set<string> &now, set<string> &pre) {
    for (int i = 0; i < 3; i++) {
        if (v[i].size() > 1) {
            for (int j = 0; j < 3; j++) {
                if (i == j || v[i][v[i].size() - 1] < v[j][v[j].size() - 1]) continue;
                if (i == 0 && j == 2) continue;
                if (i == 2 && j == 0) continue;
                v[j].push_back(v[i][v[i].size() - 1]);
                v[i].erase(v[i].end() - 1);
                if (pre.find(stom(v)) == pre.end()) {
                    next.push_back(v);
                    now.insert(stom(v));
                }
                v[i].push_back(v[j][v[j].size() - 1]);
                v[j].erase(v[j].end() - 1);
            }
        }
    }
}


