int n, m;
int memo3[16] = {0,      1,      3,       9,
                 27,     81,     243,     729,
                 2187,   6561,   19683,   59049,
                 177147, 531441, 1594323, 4782969};
bool memo[15000000] = {0};
int from[4] = {0, 1, 1, 2};
int to[4] = {1, 0, 2, 1};
struct dish {
  vector<int> v[3];
  int now;
};
dish first;

int solve();
int calc3(dish x);

int main() {
  int a, x;
  while(1) {
    cin >> n >> m;
    if(n + m == 0) break;
    for(int i = 0; i < 3; ++i) {
      cin >> x;
      for(int j = 0; j < x; ++j) {
        cin >> a;
        first.v[i].push_back(a);
      }
    }
    first.now = 0;
    memo[calc3(first)] = 1;
    cout << solve() << endl;
    for(int i = 0; i < 3; ++i)
      first.v[i].erase(first.v[i].begin(),
                       first.v[i].end());
    for(int i = 0; i < 15000000; ++i) memo[i] = 0;
  }
  return 0;
}

int solve() {
  dish now = first, next;
  bool end = 0;
  int ans = -1;
  queue<dish> qu;
  qu.push(first);
  while(qu.size() > 0 && now.now <= m && !end) {
    now = qu.front();
    qu.pop();
    if(now.v[0].size() == n || now.v[2].size() == n) {
      ans = now.now;
      end = 1;
      break;
    }
    for(int i = 0; i < 4; ++i) {
      int f = from[i], t = to[i];
      next = now;
      if(next.v[f].size() != 0 &&
         (next.v[t].size() == 0 ||
          next.v[f][next.v[f].size() - 1] >
              next.v[t][next.v[t].size() - 1])) {
        int x = next.v[f][next.v[f].size() - 1], y;
        next.v[f].pop_back();
        next.v[t].push_back(x);
        ++next.now;
        y = calc3(next);
        if(memo[y] == 0) {
          qu.push(next);
          memo[y] = 1;
        }
      }
    }
  }
  return ans;
}
int calc3(dish x) {
  int ans = 0;
  for(int i = 0; i < 3; ++i) {
    for(int j = 0; j < x.v[i].size(); ++j)
      ans += i * memo3[x.v[i][j]];
  }
  return ans;
}

