using namespace std;

const int from[]={0,2,1,1},to[]={1,1,2,0};

stack<int> tray[3];

inline bool move(int i){
	const int &f=from[i],&t=to[i];
	if(!tray[f].empty() && (tray[t].empty() || tray[f].top()>tray[t].top())){
		tray[t].push(tray[f].top());
		tray[f].pop();
		return true;
	}
	return false;
}

inline bool over(){
	return tray[1].empty() && (tray[0].empty() || tray[2].empty());
}

int simulate(int prev){
	for(int k=0;;k++){
		if(over()) return k;
		rep(i,4) if(i!=3-prev && move(i)) { prev=i; break; }
	}
}

