		int ans=(1<<31)-1;
		rep(i,4){
			stack<int> tmp[3];
			copy(tray,tray+3,tmp);
			if(move(i)) ans=min(ans,simulate(i)+1);
			copy(tmp,tmp+3,tray);
		}
