struct State {
    array<vector<i64>,3> v;
    pair<i64,i64> pre;

    vector<State> neighbor() const {
        constexpr pair<i64,i64> MOVES[] {
            {0,1}, {1,0}, {1,2}, {2,1},
        };

        vector<State> res;

        for(const auto& m : MOVES) {
            i64 from,to; tie(from,to) = m;
            i64 pfrom,pto; tie(pfrom,pto) = pre;
            if(from == pto && to == pfrom) continue;

            if(movable(from,to)) {
                State st(*this);
                st.mv(from,to);
                res.emplace_back(st);
            }
        }

#if 0
        if(movable(0,1)) {
            State st(*this);
            st.mv(0,1);
            res.emplace_back(st);
        }
        else if(movable(1,0)) {
            State st(*this);
            st.mv(1,0);
            res.emplace_back(st);
        }

        if(movable(1,2)) {
            State st(*this);
            st.mv(1,2);
            res.emplace_back(st);
        }
        else if(movable(2,1)) {
            State st(*this);
            st.mv(2,1);
            res.emplace_back(st);
        }
#endif

        return res;
    }

    void mv(i64 from, i64 to) {
        v[to].emplace_back(POP_BACK(v[from]));
        pre = { from, to };
    }

    bool movable(i64 from, i64 to) const {
        if(v[from].empty()) return false;
        if(v[to].empty()) return true;
        return v[to].back() < v[from].back();
    }

    bool done() const {
        return v[1].empty() && (v[0].empty() || v[2].empty());
    }
};

