  }
};
map<int,map<int,map<int,int> > > M;
bool check(state p){
    int a[3]={};
    for(int i=0;i<3;i++){
      for(int j=0;j<(int)p.st[i].size();j++){
	a[i] |= (1<<p.st[i][j]); 
      }
    }
    if(M[a[0]][a[1]][a[2]]!=0) return false;
    M[a[0]][a[1]][a[2]]=1;
    return true;
}
int solve(state s){
  queue<state> q;
  while(!q.empty()) q.pop();
  q.push(s);
  while(!q.empty()){
    state p = q.front(); q.pop();
    //    p.print();
    int c=p.co;
    if(c>m) break;
    if((int)p.st[0].size()==n || (int)p.st[2].size()==n) return c;
    p.co++;
    state tmp = p;
    if( !p.st[0].empty() && p.st[1].empty() ){
      goto change1;
    }else if( p.st[0].empty() && !p.st[1].empty()){
      goto change2;
    }
    if(!p.st[0].empty() && !p.st[1].empty()){
      if(p.st[0].back() > p.st[1].back()){
      change1:
	tmp.st[1].push_back(tmp.st[0].back());
	tmp.st[0].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      } else {
      change2:
	tmp.st[0].push_back(tmp.st[1].back());
	tmp.st[1].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      }
    }

    if( !p.st[1].empty() && p.st[2].empty() ){
      goto change3;
    }else if( p.st[1].empty() && !p.st[2].empty()){
      goto change4;
    }    
    if( !p.st[1].empty() && !p.st[2].empty() ){
      if(p.st[1].back() > p.st[2].back()){
      change3:
	tmp.st[2].push_back(tmp.st[1].back());
	tmp.st[1].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      } else {
      change4:
	tmp.st[1].push_back(tmp.st[2].back());
	tmp.st[2].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      }
    }

  }
  return -1;
}
