#define rep2(x,from,to) for(int x=(from);(x)<(to);(x)++)
#define rep(x,to) rep2(x,0,to)

int calc(vector<vector<int> > v, int n, int m) {
	int cnt = 0;
	while(cnt <= m){
		int i = cnt % 2, j = i + 1;
		if(i % 2 == 0) {
			if(v[i].size() == n || v[i + 2].size() == n) {
				return cnt;
			}
		} else {
			if(v[j].size() == n || v[2 - j].size() == n) {
				return cnt;
			}
		}

		auto it1 = v[i].end();
		auto it2 = v[j].end();
		if(v[i].empty()) {
			int y = *--it2;
			v[j].erase(it2);
			v[i].push_back(y);
		} else if(v[j].empty()) {
			int x = *--it1;
			v[i].erase(it1);
			v[j].push_back(x);
		} else {
			int x = *--it1, y = *--it2;
			if(x > y) {
				v[i].erase(it1);	
				v[j].push_back(x);
			} else {
				v[j].erase(it2);
				v[i].push_back(y);
			}
		}
		cnt++;
	}
	return cnt;
}
