			rep(j,c) {
				int t;
				cin >> t;
				v[i].push_back(t);
			}
		}
		int ans1 = calc(v, n, m);
		swap(v[0], v[2]);
		int ans2 = calc(v, n, m);
		int ans = min(ans1, ans2);
		if(ans <= m) {
			cout << ans;
		} else {
			cout << -1;
		}
		cout << endl;
	}
	return 0;
}

