typedef long long ll;
int toC(int A, int B, int C){
  if(!A&&!B)return 0;
  if(C&1)return toC(A>>1,B>>1,C>>1);
  else if(B&1)return toC(C>>1,B>>1,A>>1)+1+toC((A|B|C)>>1,0,0);
  else if(A&1)return toC(A>>1,B>>1,C>>1)+2*toC((A|B|C)>>1,0,0)+2;
  return 0;
}
