        a--;
        cup[i]|=1<<a;
      }
    }
    //AC
    int x=min(toC(cup[0],cup[1],cup[2]),toC(cup[2],cup[1],cup[0]));
    cout<<(x<=m?x:-1)<<endl;
  }
  return 0;
}

