template <typename T>
T power(T a, T n, T mod) {
  T res = 1;
  T tmp = n;
  T curr = a;
  while(tmp){
    if(tmp % 2 == 1){
      res = (T)((ll)res * curr % mod);
    }
    curr = (T)((ll)curr * curr % mod);
    tmp >>= 1;
  }
  return res;
}

int main(int argc, char const* argv[])
{
  ios_base::sync_with_stdio(false);
  cin.tie(0);
  cout << fixed << setprecision(20);
  int n, m;
  cin >> n >> m;
  while(n > 0 and m > 0){
    vector<int> st(3, 0);
    rep(i, 3){
      INT(c);
      rep(j, c){
        INT(x); x--; st[i] |= bit(x);
      }
    }
    auto calc = [&](vector<int> v) -> ll{
      ll res = 0;
      rep(i, n){
        int tat = -1, cat = -1;
        rep(j, 3){
          if(st[j] & bit(i))tat = j;
          if(v[j] & bit(i))cat = j;
        }
        if(tat == cat)continue;
        if(cat == 0){
          if(tat == 1){
            res += power<ll>(3, n - 1 - i, mod);
            v[cat] ^= ((bit(n)-1) ^ (bit(i)-1));
            v[tat] ^= bit(i);
            v[2] ^= ((bit(n)-1) ^ (bit(i+1)-1));
          }else{
            res += 2 * power<ll>(3, n - 1 - i, mod);
            v[cat] ^= bit(i);
            v[tat] ^= bit(i);
          }
        }else if(cat == 2){
          if(tat == 1){
            res += power<ll>(3, n - 1 - i, mod);
            v[cat] ^= ((bit(n)-1) ^ (bit(i)-1));
            v[tat] ^= bit(i);
            v[0] ^= ((bit(n)-1) ^ (bit(i+1)-1));
          }else{
            res += 2 * power<ll>(3, n - 1 - i, mod);
            v[cat] ^= bit(i);
            v[tat] ^= bit(i);
          }
        }
      }
      return res;
    };
    vector<int> cur(3, 0);
    cur[0] = bit(n) - 1;
    ll res = calc(cur);
    swap(cur[0], cur[2]);
    res = min(res, calc(cur));
    cout << (res <= m ? res: -1) << endl;
    cin >> n >> m;
  }
  return 0;
}

