using namespace std;
int n,m,ans; vector<int> cup[3];
void move(int k,int t){
	if(k>n||ans>m)return;
	int pk,o;
	rep(i,3)if(!cup[i].empty()&&cup[i][cup[i].size()-1]==k)pk=i;
	rep(i,3)if(i!=pk&&i!=t)o=i;
	cup[pk].pop_back();
	if(pk==t){
		move(k+1,t);
		cup[pk].push_back(k);
		return;
	}
	ans++;
	if(abs(pk-t)==1){
		move(k+1,o);
		cup[t].push_back(k);
	}else{
		move(k+1,t);
		cup[o].push_back(k);
	}
	move(k,t);
}
