			reverse(cup[i].begin(),cup[i].end());
		}
		ans=0;move(1,0); int pw=1;rep(i,n)pw*=3;
		cout<<(ans>m?-1:min(ans,pw-1-ans))<<endl;
	}
	return 0;
}

