                if (!s[j].empty()&&s[i].top()<s[j].top()) continue; // 
                s[j].push(s[i].top()); s[i].pop();
                res=min(res, dfs(i,j,1));
                s[i].push(s[j].top()); s[j].pop();
            }
        }
        printf("%d\n",res==m+1?-1:res);
    }
    return 0;
}

