typedef vector<vector<int>> mat;

const int INF = 1 << 28;
int n, m;

bool check(mat &v) {
    if (v[0].size() == n + 1 || v[2].size() == n + 1)
        return true;
    return false;
}

string stom(mat &v) {
    string res;
    res = "";
    for (int i = 0; i < 3; i++) {
        for (auto j : v[i]) {
            if (j < 0) continue;
            res = res + (char)('0' + j);
        }
        for (int j = v[i].size(); j <= n; j++) {
            res = res + '0';
        }
    }
    return res;
}

map<string , int> M;

int dfs(mat &v, int d) {
    string V = stom(v);
    if (d > m) return M[V] = INF;
    if (check(v)) return M[V] = d;
    if (M.find(V) != M.end()) return M[V];
    M[V] = 1 << 28;

    // cout << "# " << d << endl;
    for (int i = 0; i < 3; i++) {
        if (v[i].size() > 1) {
            for (int j = 0; j < 3; j++) {
                // cout << "form: " << i << ", to: " << j << endl;
                if (i == j || v[i][v[i].size()-1] < v[j][v[j].size() - 1]) continue;
                if (i == 0 && j == 2) continue;
                if (i == 2 && j == 0) continue;
                v[j].push_back(v[i][v[i].size() - 1]);
                v[i].erase(v[i].end() - 1);
                M[V] = min(M[V], dfs(v, d + 1));
                v[i].push_back(v[j][v[j].size() - 1]);
                v[j].erase(v[j].end() - 1);
            }
        }
    }

    return M[V];
}


