const ll INF = 1e9;
const ll MOD = 1e9 + 7;

void solve(int n,int m) {
    vector<int> power3(n + 1,1); rep(i,n) power3[i + 1] = power3[i]*3;
    int init_state = 0;
    int a; cin >> a;
    rep(i,a) {int num; cin >> num;}
    int b; cin >> b;
    rep(i,b) {int num; cin >> num; init_state += power3[num - 1];}
    int c; cin >> c;
    rep(i,c) {int num; cin >> num; init_state += power3[num - 1]*2;}

    vector<int> dist(power3[n],-1);
    dist[init_state] = 0;
    queue<int> que;
    que.push(init_state);
    while (que.size()) {
        int p = que.front(); que.pop();
        int a_max = -1,b_max = -1,c_max = -1;
        for (int i = 0;i < n; i++) {
            if ((p/power3[i])%3 == 0) a_max = i;
            else if ((p/power3[i])%3 == 1) b_max = i;
            else  c_max = i;
        }

        if (a_max < b_max) {
            int q = p - power3[b_max];
            if (dist[q] < 0) {
                dist[q] = dist[p] + 1;
                que.push(q);
            }
        }

        if (a_max > b_max) {
            int q = p + power3[a_max];
            if (dist[q] < 0) {
                dist[q] = dist[p] + 1;
                que.push(q);
            }
        }

        if (b_max > c_max) {
            int q = p + power3[b_max];
            if (dist[q] < 0) {
                dist[q] = dist[p] + 1;
                que.push(q);
            }
        }

        if (b_max < c_max) {
            int q = p - power3[c_max];
            if (dist[q] < 0) {
                dist[q] = dist[p] + 1;
                que.push(q);
            }
        }
    }

    if (dist[0] < 0 && dist.back() < 0) cout << -1 << endl;
    else if (dist[0] < 0) {if (dist.back() <= m) cout << dist.back() << endl;}
    else if (dist.back() < 0) {if (dist[0] <= m) cout << dist[0] << endl;}
    else if (min(dist[0],dist.back()) <= m) cout << min(dist[0],dist.back()) << endl;
    else cout << -1 << endl;
}

int main() {
    while (1) {
        int n,m; cin >> n >> m;
        if (n == 0) return 0;
        solve(n,m);
    }
}

