int toC(int A,int B,int C) {
	if(A==0&&B==0) return 0;
	if(C&1) return toC(A>>1,B>>1,C>>1);
	if(B&1) return toC(C>>1,B>>1,A>>1)+1+toC((A|B|C)>>1,0,0);
	if(A&1) return toC(A>>1,B>>1,C>>1)+2*toC((A|B|C)>>1,0,0)+2;
}
