#include <cstdio>
#include <queue>
#include <algorithm>
using namespace std;

int tostate(int a, int b, int c, int k)
{
	int mul;
	int ret = 0;

	for(int i = 0, mul = 1; i < k; ++i, mul *= 3)
		ret += ((a >> i) & 1) * 1 * mul;
	for(int i = 0, mul = 1; i < k; ++i, mul *= 3)
		ret += ((b >> i) & 1) * 2 * mul;

	return ret;
}

inline int mhb(int x)
{
	for(int i = 31; i >= 0; --i) {
		if(x & (1 << i))
			return 1 << i;
	}

	return 0;
}

const int inf = 1000000000;
const int pow3x5 = 3 * 3 * 3 * 3 * 3;
const int pow3x15 = pow3x5 * pow3x5 * pow3x5;
bool visit[pow3x15];

struct node {
	int a, b, c, remain;
	node(int a, int b, int c, int remain) : a(a), b(b), c(c), remain(remain) {}
};

int calc(int a, int b, int c, int remain, int k)
{
	int f = inf;

	queue<node> q;

	q.push(node(a, b, c, remain));

	while(!q.empty()) {

		node cur = q.front();
		q.pop();

		if(cur.remain < 0)
			continue;

		if(cur.b == 0 && (cur.a == 0 || cur.c == 0)) {
			f = cur.remain;
			break;
		}

		int state = tostate(cur.a, cur.b, cur.c, k);

		if(visit[state])
			continue;
		visit[state] = true;

		if(cur.a > cur.b) {
			int move = mhb(cur.a);
			int ta = cur.a - move;
			int tb = cur.b + move;
			int tc = cur.c;
			q.push(node(ta, tb, tc, cur.remain - 1));
		} else {
			int move = mhb(cur.b);
			int ta = cur.a + move;
			int tb = cur.b - move;
			int tc = cur.c;
			q.push(node(ta, tb, tc, cur.remain - 1));
		}

		if(cur.b > cur.c) {
			int move = mhb(cur.b);
			int ta = cur.a;
			int tb = cur.b - move;
			int tc = cur.c + move;
			q.push(node(ta, tb, tc, cur.remain - 1));
		} else {
			int move = mhb(cur.c);
			int ta = cur.a;
			int tb = cur.b + move;
			int tc = cur.c - move;
			q.push(node(ta, tb, tc, cur.remain - 1));
		}
	}

	int ret = (f != inf ? remain - f : -1);

	return ret;
}

int main()
{
