int n, m, k, x;

int chk(vector<stack<int>> tray) {
    int ret = 0;
    while (1) {
        for (int i=0; i<=1; ++i) {
            if (tray[0].size() == n+1 || tray[2].size() == n+1) return ret;
            int a = tray[i].top(); tray[i].pop();
            int b = tray[i+1].top(); tray[i+1].pop();
            if (a < b) {
                tray[i].push(a);
                tray[i].push(b);
            } else {
                tray[i+1].push(b);
                tray[i+1].push(a);
            }
            ret ++;
        }
        if (m < ret) return 15000001;
    }
}

