				date[flag].pop_back();
			}
			count++;
			flag = !flag;
		}
		printf("%d\n", count <= m ? count : -1);
	}
}

#include<iostream>
#include<cstdlib>
#include<cstdio>
#include<queue>
#include<algorithm>

#define MAX 16
#define A 0
#define B 1
#define C 2
#define P_MAX 3

using namespace std;

template<typename _T>
_T &abs(const _T &x){if(x<0)return -x;else return x;}

int cost[3][MAX];

struct Cups{
	Cups(){
		cost = 0;
		v[0].reserve(MAX);
		v[1].reserve(MAX);
		v[2].reserve(MAX);
	}
	vector<int> v[P_MAX];
	int cost;
};

int BFS(int n, int m, Cups &st){
	int ret = 0;
	queue<Cups> q;
	q.push( st );

	while( !q.empty() ){
		Cups cups;
		cups = q.front();
		q.pop();

		/* I */
		if( cups.v[A].size() == n || cups.v[C].size() == n ){
			ret = cups.cost;
			break;
		}

		if( cups.cost > m )
			continue;

		//cout << "BFS: " << cups.cost << endl;

		for(int i = A; i <= C; ++i){
			int mov_cnt = 1;
			int min_sz = -1;
			for(int j = cups.v[i].size()-1; j >= 1; --j){
				if( cups.v[i][j] - cups.v[i][j-1] == 1 ){
					++mov_cnt;
					min_sz = cups.v[i][j-1];
				}else{
					break;
				}
			}
			if( cups.v[i].size() == 1 )
				min_sz = cups.v[i][ 0 ];

			if( min_sz == -1 )
				continue;
			//cout << "From " << ((char)(i + 'A')) << ' ' << mov_cnt;

			for(int j = A; j <= C; ++j){
				if( i != j ){
					int d = abs( i - j );
					Cups t = cups;
					int end = t.v[j].size() - 1;
					if( end >= 0 && t.v[j][end] > min_sz )
						continue;

					//cout << " To " << (char(j+'A')) << endl;
					t.v[j].insert( t.v[j].end(), t.v[i].end() - mov_cnt, t.v[i].end() );
					t.v[i].erase( t.v[i].end() - mov_cnt, t.v[i].end() );
					t.cost += cost[ d ][ mov_cnt ];
					q.push( t );
				}
			}
		}
	}
	return ret;
}

int main(){
	cost[1][1] = 1;
	cost[2][1] = 2;
	for(int i = 1; i < 3; ++i){
		for(int j = 2; j < MAX; ++j){
			if( i == 1 ){
				cost[i][j] = 3 * cost[i][j - 1] + 1;
			}else{
				cost[i][j] = 2 * cost[1][j];
			}
		}
	}

	while(true){
		int n,m;
		int ans = 0;
		Cups cups;

		scanf("%d%d", &n, &m);
		if( n == 0 && m == 0 )
			break;

		for(int i = 0; i < 3; ++i){
			int t;
			scanf("%d", &t);
			for(int j = 0; j < t; ++j){
				int tt;
				scanf("%d", &tt);
				cups.v[i].push_back(tt);
			}
		}
		ans = BFS(n,m,cups);

		printf("%d\n", ans <= m ? ans : -1);
	}
	return 0;
}

