int T[16];
int A[16], B[16];
int n, m;

int solve() {
    int P[17];
    P[n-1] = 2;
    for ( int i = n-1; i > 0; -- i ) P[i-1] = 3 * P[i] + 2;

    A[n-1] = T[n-1];
    B[n-1] = 2 - T[n-1];
    for ( int i = n; i > 1; -- i ) {
        if ( T[i-2] == 0 ) {
            A[i-2] = A[i-1];
            B[i-2] = A[i-1] + 2 * P[i-1] + 2;
        } else if ( T[i-2] == 1 ) {
            A[i-2] = B[i-1] + P[i-1] + 1;
            B[i-2] = A[i-1] + P[i-1] + 1;
        } else {
            A[i-2] = A[i-1] + 2 * P[i-1] + 2;
            B[i-2] = B[i-1];
        }
    }
    int res = min( A[0], B[0] );
    if ( res > m ) return -1;
    return res;
}

