int cost[3][MAX];
int main(){
	cost[1][1] = 1;
	cost[2][1] = 2;
	for(int i = 1; i < 3; ++i){
		for(int j = 2; j < MAX; ++j){
			if( i == 1 ){
				cost[i][j] = 3 * cost[i][j - 1] + 1;
			}else{
				cost[i][j] = 2 * cost[1][j];
			}
		}
	}

	while(true){
		int ans = 0;
		int places[MAX];
		int maxs[3] = {0,};
		int nums[3] = {0,};
