			break;

		for(int i = 0; i < 3; ++i){
			int t;
			scanf("%d", &t);
			for(int j = 0; j < t; ++j){
				scanf("%d", &cups[i][j]);
				places[ cups[i][j] ] = i;
				nums[i]++;
				maxs[i] = max(maxs[i],cups[i][j]);
			}
		}

		if( nums[B] == 1 ){
			if( !( maxs[A] < maxs[B] && maxs[C] < maxs[B] ) ){
				if( maxs[A] < maxs[B] ){
					places[ cups[B][0] ] = A;
					ans += 1;
				}else if( maxs[C] < maxs[B] ){
					places[ cups[B][0] ] = C;
					ans += 1;
				}
			}
		}

		for(int i = n; i >= 2; --i){
			int d = abs( places[ i ] - places[i - 1] );
			ans += cost[d][n - i + 1];
			places[ i ] = places[ i - 1 ];
		}

		if( places[ 1 ] == B ){
			ans += cost[1][n];
		}

		printf("%d\n", ans <= m ? ans : -1);
	}
	return 0;
}

