using vii=vector<vector<int>>;

const ll LINF=1LL<<60;
const int INF=1<<29;
const int MOD=1e9+7;

vii cup;
int n, m;

ll solve(int tray){
    ll res=0;
    vii tmp=cup;
    for(int i=1; i<=n; i++){
        int pos;
        for(int j=0; j<3; j++){
            if(tmp[j].empty()) continue;
            if(tmp[j].back() == i){
                pos=j;
                tmp[j].pop_back();
                break;
            }
        }
        if(pos == tray) continue;
        else if(abs(pos-tray) == 1){
            res+=pow(3, n-i);
            tray=tray==0?2:0;
        }
        else{
            res+=pow(3,n-i)*2;
        }
        if(res > m) break;
    }
    return res;
}

int main(){
    while(1){
        cin >> n >> m;
        if(n == 0 && m == 0) break;
        cup.assign(3, vi(0));
        for(int i=0; i<3; i++){
            int c; cin >> c;
            cup[i].resize(c);
            for(auto &x : cup[i]) cin >> x;
            reverse(all(cup[i]));
        }

        ll ans=solve(0);
        ans=min(ans, solve(2));
        if(ans > m) cout << -1 << endl;
        else cout <<ans << endl;
    }
    return 0;
}

