using namespace std;

#define REP(i,n) for(int i=0;i<(int)(n);++i)

int encode(const vector<int> &state) {
  int h = 0;
  REP(i, state.size())
      h = h * 3 + state[i];
  return h;
}

int decode(int h, vector<int>& out) {
  int N = out.size();
  REP(i, N) {
      out[N - 1 - i] = h % 3;
      h /= 3;
  }
}

vector<int> move(vector<int> v, int from, int to) {
  int maxFrom = -1, maxTo = -1;
  REP(i, v.size()) {
      if (v[i] == from && maxFrom < i) maxFrom = i;
      if (v[i] == to && maxTo < i) maxTo = i;
  }

  if (maxFrom > maxTo) {
      v[maxFrom] = to;
  }
  return v;
}

bool finish(vector<int> state) {
  int N = state.size();
  return N == count(state.begin(), state.end(), 0) ||
      N == count(state.begin(), state.end(), 2);
}

bool visited[15000000];

int main() {
  for (int N, M; cin >> N >> M, (N || M); ) {
      memset(visited, 0, sizeof(visited));

      vector<int> state(N, -1);
      for (int i = 0; i < 3; i++) {
          int K; cin >> K;
          while (K--) {
              int a; cin >> a;
              state[a - 1] = i;
          }
      }
      queue<int> q;

      visited[encode(state)] = true;
      q.push(encode(state));
      q.push(0);

      int ans = -1;
      while (!q.empty()) {
          decode(q.front(), state); q.pop();
//          REP(i, N) cout << state[i] << " "; cout << endl;
          int step = q.front(); q.pop();
          if (step > M) break;
          if (finish(state)) {
              ans = step;
              break;
          }

          int next;
          next = encode(move(state, 0, 1));
          if (!visited[next]) {visited[next] = true; q.push(next); q.push(step + 1);}

          next = encode(move(state, 1, 0));
          if (!visited[next]) {visited[next] = true; q.push(next); q.push(step + 1);}

          next = encode(move(state, 2, 1));
          if (!visited[next]) {visited[next] = true; q.push(next); q.push(step + 1);}

          next = encode(move(state, 1, 2));
          if (!visited[next]) {visited[next] = true; q.push(next); q.push(step + 1);}
      }
      cout << ans << endl;

  }
  return 0;
}

