  if(s.a == (1 << n) - 1 || s.c == (1 << n) - 1) return s.cost;
  if(s.cost >= m) return INF;
  int res = INF;
  int tmp[N];
  fill(tmp, tmp+N, 0);
  for(int i=n-1;i>=0;i--){
    if(s.a & 1 << i){
      tmp[0] = i+1;
      break;
    }
  }
  for(int i=n-1;i>=0;i--){
    if(s.b & 1 << i){
      tmp[1] = i+1;
      break;
    }
  }
  for(int i=n-1;i>=0;i--){
    if(s.c & 1 << i){
      tmp[2] = i+1;
      break;
    }
  }
  if(tmp[0]){
    for(int j=0;j<N;j++){
      if(abs(0-j) != 1) continue;
      if(0 == pret && j == pres) continue;
      if(tmp[0] > tmp[j]){
        S t = s;
        t.a &= (~(1 << (tmp[0]-1)));
        if(j == 0) t.a |= (1 << (tmp[0]-1));
        if(j == 1) t.b |= (1 << (tmp[0]-1));
        if(j == 2) t.c |= (1 << (tmp[0]-1));
        t.cost++;
        res = min(res, calc(t, 0, j));
        //ans = min(ans, res);
      }
    }
  }  
  if(tmp[1]){
    for(int j=0;j<N;j++){
      if(abs(1-j) != 1) continue;
      if(1 == pret && j == pres) continue;
      if(tmp[1] > tmp[j]){
        S t = s;
        t.b &= (~(1 << (tmp[1]-1)));
        if(j == 0) t.a |= (1 << (tmp[1]-1));
        if(j == 1) t.b |= (1 << (tmp[1]-1));
        if(j == 2) t.c |= (1 << (tmp[1]-1));
        t.cost++;
        res = min(res, calc(t, 1, j));
        //ans = min(ans, res);
      }
    }
  }
  if(tmp[2]){
    for(int j=0;j<N;j++){
      if(abs(2-j) != 1) continue;
      if(2 == pret && j == pres) continue;
      if(tmp[2] > tmp[j]){
        S t = s;
        t.c &= (~(1 << (tmp[2]-1)));
        if(j == 0) t.a |= (1 << (tmp[2]-1));
        if(j == 1) t.b |= (1 << (tmp[2]-1));
        if(j == 2) t.c |= (1 << (tmp[2]-1));
        t.cost++;
        res = min(res, calc(t, 2, j));
        //ans = min(ans, res);
      }
    }
  }
  return res;
}

