using namespace std;

const int MAXN = 17;
const int SSIZE = 15000000;
const int INF = INT_MAX;

const int W = 3;
int N, M;

int three[MAXN];
bool vis[SSIZE];

void init() {
  three[0] = 1;
  for(int i = 1; i < MAXN; ++i) three[i] = three[i-1] * 3;
}

int get3(int s, int i) {
  return s/three[i]%3;
}

int getTop(int s, int pos) {
  int maxi = -1;
  for(int i = 0; i < N; ++i) {
    if(s % W == pos) maxi = i;
    s /= W;
  }
  return maxi;
}

int move(int s, int from, int to) {
  int a = getTop(s, from);
  int b = getTop(s, to);
  if(a == -1) return INF;
  if(a < b) return INF;
  s -= three[a] * from;
  s += three[a] * to;
  return s;
}

void show(int s) {
  for(int i = 0; i < N; ++i) {
    cout << s%3;
    s /= 3;
  }
  cout << endl;
}

int bfs(int s) {
  queue<pair<int, int> > que;
  fill(vis, vis+SSIZE, false);
  que.push(make_pair(s,0));
  vis[s] = true;

  int goal1, goal2;
  goal1 = goal2 = 0;
  for(int i = 0; i < N; ++i) goal2 += three[i]*2;
  if(s == goal1 || s == goal2) {
    return 0;
  }

  while(!que.empty()) {
    s = que.front().first;
    int cost = que.front().second;
    que.pop();
    if(cost == M) continue;
    for(int i = 0; i < W; ++i) {
      for(int j = 0; j < W; ++j) {
	if(abs(i-j) != 1) continue;
	int ns = move(s, i, j);
	if(ns == INF) continue;
	if(vis[ns]) continue;
	if(ns == goal1 || ns == goal2) {
	  return cost+1;
	}
	vis[ns] = true;
	que.push(make_pair(ns, cost+1));
      }
    }
  }
  return INF;
}

int main() {
  init();
  while(cin >> N >> M && (N|M)) {
    int sum = 0;
    for(int i = 0; i < W; ++i) {
      int k;
      cin >> k;
      while(k--) {
	int a;
	cin >> a;
	--a;
	sum += three[a] * i;
      }
    }
    int ans = bfs(sum);
    if(ans <= M) {
      cout << ans << endl;
    } else {
      cout << -1 << endl;
    }
  }
  return 0;
}

