int solve() {
    int ans = 100000000;
    for (int i = 0; i < 2; i++) {
        stack<int> a, b, c; // A, B, C
        for (int j = 0; j < a0; j++) a.push(A[j]);
        for (int j = 0; j < b0; j++) b.push(B[j]);
        for (int j = 0; j < c0; j++) c.push(C[j]);
        for (int j = 0; j <= m; j++) {
            if (a.size() == n || c.size() == n) {
                ans = min(ans, j);
                break;
            }
            if (j % 2 == i) {
                if (a.empty()) {
                    a.push(b.top());
                    b.pop();
                }
                else if (b.empty()) {
                    b.push(a.top());
                    a.pop();
                }
                else if (a.top() < b.top()) {
                    a.push(b.top());
                    b.pop();
                }
                else {
                    b.push(a.top());
                    a.pop();
                }
            }
            else {
                if (c.empty()) {
                    c.push(b.top());
                    b.pop();
                }
                else if (b.empty()) {
                    b.push(c.top());
                    c.pop();
                }
                else if (c.top() < b.top()) {
                    c.push(b.top());
                    b.pop();
                }
                else {
                    b.push(c.top());
                    c.pop();
                }
            }
        }
    }
    if (ans == 100000000) return -1;
    else return ans;
}

