			++ns.count;
			ns.from=0;
			ns.to=1;
			que.push(ns);
		}
		if(!(st.from==0&&st.to==1)&&st.s[1].size()!=0&&(st.s[0].size()==0||st.s[1].top()>st.s[0].top())){
