using namespace std;

const int NONE = -1;
typedef tuple<int, int, int> Tuple;
map<Tuple, int> S;
Tuple goal;
int n, m;

int get_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return 1 << i;
    }
    return 0;
}

int remove_last( int m ) {
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( ( m & ( 1 << i ) ) == ( 1 << i ) ) return m ^ ( 1 << i );
    }
    return m;
}

int solve( int A, int B, int C, int turns ) {
    Tuple tuple = make_tuple( A, B, C );
    if ( S.count( tuple ) > 0 && turns >= S[tuple] ) return -1;
    S[tuple] = turns;
    if ( turns > m ) return -1;
    if ( tuple == goal ) return turns;
    if ( A != 0 ) {
        int u = get_last(A);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( remove_last( A ), B | u, C, turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    if ( B != 0 ) {
        int u1 = get_last(B);
        int v1 = get_last(A);
        if ( u1 > v1 ) {
            int ret1 = solve( A | u1, remove_last(B), C, turns + 1 );
            if ( ret1 != NONE ) return ret1;
        }
        int u2 = get_last(B);
        int v2 = get_last(C);
        if ( u2 > v2 ) {
            int ret2 = solve( A, remove_last(B), C | u2, turns + 1 );
            if ( ret2 != NONE ) return ret2;
        }
    }
    if ( C != 0 ) {
        int u = get_last(C);
        int v = get_last(B);
        if ( u > v ) {
            int ret = solve( A, B | u, remove_last(C), turns + 1 );
            if ( ret != NONE ) return ret;
        }
    }
    return -1;
}

