using namespace std;

int solve(int x, int y, int z) {
	if (!x && !y) return 0;
	if (z & 1) return solve(x >> 1, y >> 1, z >> 1);
	if (y & 1) return solve(z >> 1, y >> 1, x >> 1) + solve((x | y | z) >> 1, 0, 0) + 1;
	if (x & 1) return solve(x >> 1, y >> 1, z >> 1) + 2 * solve((x | y | z) >> 1, 0, 0) + 2;
}

