			for (int j = 0; j < t; ++j) {
				int a; cin >> a;
				c[i] |= 1 << a - 1;
			}
		}
		int ans = min(solve(c[0], c[1], c[2]), solve(c[2], c[1], c[0]));
		cout << (ans <= m ? ans : -1) << endl;
	}

	return 0;
}

