int main(){
	int n,m;
	while(cin>>n>>m){
		if(n==0&&m==0) break;
		vector<vector<int> > goal(3);
		for(int i=0; i<3; ++i){
			int q;
			cin >> q;
			for(int j=0; j<q; ++j){
					int tmp;
					cin >> tmp;
					goal[i].push_back(tmp-1);
			}
		}

		vector<vector<int> > field(3);
		for(int i=0; i<n; ++i) field[0].push_back(i);

		int turn=0;
		int prev_from=-1;
		int prev_to=-1;
		while(true){
			if(field==goal) break;
			++turn;
			for(int from=0; from<3; ++from){
				if(field[from].size()==0) continue;
				const int cup=field[from].back();
				const int to_1=from-1;
				const int to_2=from+1;
				if(!(from==prev_to&&to_1==prev_from)&&to_1>=0&&(field[to_1].size()==0||field[to_1].back()<cup)){
					field[to_1].push_back(cup);
					field[from].pop_back();
					prev_from=from;
					prev_to=to_1;
					break;
				} else if(!(from==prev_to&&to_2==prev_from)&&to_2<3&&(field[to_2].size()==0||field[to_2].back()<cup)){
					field[to_2].push_back(cup);
					field[from].pop_back();
					prev_from=from;
					prev_to=to_2;
					break;
				}
			}
		}
		int MAXTURN=1;
		for(int i=0; i<n; ++i) MAXTURN*=3;
		--MAXTURN;
		turn=min(turn,MAXTURN-turn);
		if(turn>m) cout << -1 << endl;
		else cout << turn << endl;
	}
	return 0;
}

