bool ok(vvi a){
  return (a[0].empty()&&a[1].empty()) || (a[1].empty()&&a[2].empty());
}
void print(vvi a){
  REP(i,a.size()){
    printf("%d:",i+1);
    REP(j,a[i].size()){
      printf("%d ",a[i][j]);
    }
    putchar('\n');
  }
    putchar('\n');
}
int toi(vvi a){
  int res = 0;
  REP(i,a[1].size()) res += pow(3.0, a[1][i]);
  REP(i,a[2].size()) res += pow(3.0, a[2][i]) * 2;
  return res;
}
bool used[14350000];
int solve(vvi a, int m){
  queue<pvi> que;
  que.push(pvi(a,0));
  memset(used, 0, sizeof(used));
  while(!que.empty()){
    pvi p = que.front(); que.pop();
    vvi na = p.first; int nt = p.second;
    if(nt > m) return -1;
    if(ok(na)) return nt;
    int useidx = toi(na);
    if(used[useidx]) continue;
    used[useidx] = true;
    //printf("--------------------\n");
    //print(na);
    REP(i,3)REP(j,3){
      if(abs(i-j)== 1 && na[i].size() && (na[j].empty() || na[i].back() > na[j].back())){
        vvi nna = na;
        nna[j].push_back(nna[i].back());
        nna[i].pop_back();
        useidx = toi(nna);
        if(used[useidx]) continue;
        que.push(pvi(nna, nt + 1));
        //print(nna);
      }
    }
  }
  return -1;
}

