#include <cstdio>
#include <stack>
#include <algorithm>
using namespace std;

#define INF 100000000
int n, m;

int dfs(stack<int> A, stack<int> B, stack<int> C, int dir, int cost){
	int c;
	int ans = INF;

	if(cost > m) return ans;
	if(A.size() == n) return cost;

	if(dir != 1 && !B.empty() && (A.empty() || A.top() < B.top())){//do 4
		c = B.top(); A.push(c); B.pop();
		ans = min(ans, dfs(A, B, C, 4, cost + 1));
		c = A.top(); B.push(c); A.pop();
	}
	if(dir != 2 && !C.empty() && (B.empty() || B.top() < C.top())){//do 3
		c = C.top(); B.push(c); C.pop();
		ans = min(ans, dfs(A, B, C, 3, cost + 1));
		c = B.top(); C.push(c); B.pop();
	}
	if(dir != 3 && !B.empty() && (C.empty() || C.top() < B.top())){//do 2
		c = B.top(); C.push(c); B.pop();
		ans = min(ans, dfs(A, B, C, 2, cost + 1));
		c = C.top(); B.push(c); C.pop();
	}
	if(dir != 4 && !A.empty() && (B.empty() || B.top() < A.top())){//do 1
		c = A.top(); B.push(c); A.pop();
		ans = min(ans, dfs(A, B, C, 1, cost + 1));
		c = B.top(); A.push(c); B.pop();
	}

	return ans;
}

int main(){
	int a, b;
	stack<int> A, B, C;
	int res;

	while(true){
		scanf("%d%d", &n, &m);
		if(n == 0 && m == 0) break;

		while(!A.empty()) A.pop();
		while(!B.empty()) B.pop();
		while(!C.empty()) C.pop();

		scanf("%d", &a);
		for(int i = 0; i < a; i++){
			scanf("%d", &b);
			A.push(b);
		}
		scanf("%d", &a);
		for(int i = 0; i < a; i++){
			scanf("%d", &b);
			B.push(b);
		}
		scanf("%d", &a);
		for(int i = 0; i < a; i++){
			scanf("%d", &b);
			C.push(b);
		}

		res = dfs(A, B, C, 0, 0);
		printf("%d\n", (res == INF)? -1 : res);
	}

	return 0;
}

