int judge(int max, int temp, int l, int r){
	if(a[an - l] == max - temp)return 50 + temp;
	if(c[cn - r] == max - temp)return 100 + temp;
	if(temp + 1 == an + bn + cn)return -1;
	if(b[bn - temp + l + r] == max - temp)return judge(max, temp + 1, l, r);
	return 0;
}

int calculation(int min, int max){
	int sum;
	if(min == max)return 0;
	if(min < max)sum = calculation(++min, max) * 3 + 2;
	return sum;
}

int move(int flag){
	int number;
	if(flag == 2){  //right
		number = an - m1.nu + 1;
		for(int i = 1;i <= number;i++)c[++cn] = a[an--];
	}
	if(flag == 1){
		number = cn - m1.nu + 1;
		for(int i = 1;i <= number;i++)a[++an] = c[cn--];
	}
	//printf("*%d %d %d*", an, m1.nu, number);
	total += calculation(0, number);
	//printf("*%d*", number);
	return 0;
}

