	if(m1.ch == m2.ch)return 0;
	if(m2.ch == 'b'){
		if(m1.ch == 'a')c[++cn] = b[bn--], move(2);  //right
		if(m1.ch == 'c')a[++an] = b[bn--], move(1);
		total++;
	}else if(m2.ch == 'a'){
		if(c[cn - 1] == max - 2){
			int te = an;
			b[++bn] = a[an--], move(1);
			m1.ch = 'a', m1.nu = te;
			c[++cn] = b[bn--], move(2);
		}else{
			move(1);
		}
	}else if(m2.ch == 'c'){
		if(a[an - 1] == max - 2){
			int te = cn;
			b[++bn] = c[cn--], move(2);
			m1.ch = 'c', m1.nu = te;
			a[++an] = b[bn--], move(1);
