int n,m,ans;
vector< vector<int> > inv, v;

void solve(int a,int b,int cost){
	if(cost >= ans) return;
	if(inv == v){
		ans = min(ans,cost);
		return;
	}

	for(int i=0;i<2;i++){
		int popA = v[i].empty() ? -1 : v[i].back();
		int popB = v[i+1].empty() ? -1 : v[i+1].back();

		if(popA > popB && (i != b || i+1 != a)){
			v[i+1].push_back(v[i].back()); v[i].pop_back();
			solve(i,i+1,cost+1);
			v[i].push_back(v[i+1].back()); v[i+1].pop_back();
		}
		else if(popB > popA && (i+1 != b || i != a)){
			v[i].push_back(v[i+1].back()); v[i+1].pop_back();
			solve(i+1,i,cost+1);
			v[i+1].push_back(v[i].back()); v[i].pop_back();
		}
	}
}

