  }
};

lli n, m, ans;

lli calc(S s, lli pres, lli pret){
  if(ans <= s.cost) return INF;
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  if(s.cost == m) return INF;
  lli res = INF;
  lli tmp[3];
  fill(tmp, tmp+3, -1);
  for(lli i=n-1;i>=0;i--){
    for(lli j=0;j<3;j++){
      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(lli i=0;i<3;i++){
    for(lli j=0;j<3;j++){
      if(abs(i-j) != 1) continue;
      if(i == pret && j == pres) continue;
      if(tmp[i] > tmp[j]){
        S t = s;
        t.t[i] &= ~(1 << tmp[i]);
        t.t[j] |= 1 << tmp[i];
        t.cost++;
        res = min(res, calc(t, i, j));
